import React from 'react'

export default function Forgot_Pass() {
    const [values,setValue] = useState({
        email:"s@sourav.in",
        otp:"",
        error:"",
        isLoading:false,
        otpSend:false,
        didRedirect:false
    })

    const {email,otp,error,isLoading,otpSend,didRedirect}=values

    const handleChange =(field)=>(event)=>{
        setValue({...values,error:false,[field]:event.target.value})
    }

    const performRedirect = ()=>{
        if(didRedirect){
            return <Redirect to="/otp_verify" />
        }
    }

    const successMsg =()=>{
        return (
            isLoading && (
                <div className="container alert alert-info">
                    <h2>Loading...</h2>
                </div>
            )
        )
    }
    const errorMsg = ()=>{
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div 
                    style={{display: error ? "" :"none" }}
                    className="alert alert-danger text-center">
                        {JSON.stringify(error)}
                    </div>
                </div>
            </div>
        )
    }

    const sendOTPForm= ()=>{
        return (
            <div className="row">
                <div className="col-md-6 text-left offset-sm-3">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input value={email} onChange={handleChange("email")} type="email" className="form-control"/>
                        </div>
                        <button onClick={onSubmitEmail} className="btn btn-success btn-block">Send OTP</button>
                    </form>
                </div>
            </div>
        );
    }

    const otpVerifyForm= ()=>{
        return (
            <div className="row">
                <div className="col-md-6 text-left offset-sm-3">
                    <form>
                        <div className="form-group">
                            <label className="text-light">OTP</label>
                            <input value={otp} onChange={handleChange("otp")} type="number" className="form-control"/>
                        </div>
                        <button onClick={onSubmitOTP} className="btn btn-success btn-block">Verify OTP</button>
                    </form>
                </div>
            </div>
        );
    }

    const onSubmitEmail=(event)=>{
        event.preventDefault()
        setValue({...values,error:false,isLoading:true})
        //TODO: Methord
    }

    const onSubmitOTP=(event)=>{
        event.preventDefault()
        setValue({...values,error:false,isLoading:true})
        //TODO: Methord
    }

    return !otpSend
    ?(
        <Base title="Forgot Password">
            {successMsg()}
            {errorMsg()}
            {sendOTPForm()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    )
    :(
        <Base title="Forgot Password">
            {successMsg()}
            {errorMsg()}
            {otpVerifyForm()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    )
}
