import {API} from '../../backend'

export const signup = (user)=>{
    return fetch(`${API}/singup`,{
        method:"POST",
        headers:{
            Accept:"application/json",
            "Content-Type":"application/json"
        },
        body:JSON.stringify(user)
    }).then((responce)=>{
        return responce.json()
    }).catch((err)=>{
        console.log(err);
    })
}

export const singin = (user)=>{
    return fetch(`${API}/singin`,{
        method:"POST",
        headers:{
            Accept:"application/json",
            "Content-Type":"application/json"
        },
        body:JSON.stringify(user)
    }).then((responce)=>{
        return responce.json()
    }).catch((err)=>{
        console.log(err);
    })
}

export const fogortpass = (email)=>{
    return fetch(`${API}/forgot_pass`,{
        method:"POST",
        headers:{
            Accept:"application/json",
            "Content-Type":"application/json"
        },
        body:JSON.stringify({email})
    }).then((responce)=>{
        return responce.json()
    }).catch((err)=>{
        console.log(err);
    })
}

export const veifyotp = (email,otp)=>{
    return fetch(`${API}/verify_otp`,{
        method:"POST",
        headers:{
            Accept:"application/json",
            "Content-Type":"application/json"
        },
        body:JSON.stringify({email,otp})
    }).then((responce)=>{
        return responce.json()
    }).catch((err)=>{
        console.log(err);
    })
}

export const changepass=(email,newpass,otp)=>{
    return fetch(`${API}/change_pass/${otp}`,{
        method:"POST",
        headers:{
            Accept:"application/json",
            "Content-Type":"application/json"
        },
        body:JSON.stringify({email,newpass})
    }).then((responce)=>{
        return responce.json()
    }).catch((err)=>{
        console.log(err);
    })
}
