import React, { useState } from 'react'
import Base from '../re_usable/Base'

export default function Change_Pass() {
    const [values,setValue] = useState({
        password:"12345",
        error:"",
        isLoading:false,
        didRedirect:false
    })

    const {password,error,isLoading,didRedirect}=values

    const handleChange =(field)=>(event)=>{
        setValue({...values,error:false,[field]:event.target.value})
    }

    const performRedirect = ()=>{
        if(didRedirect){
            return <Redirect to="/signin" />
        }
    }

    const successMsg =()=>{
        return (
            isLoading && (
                <div className="container alert alert-info">
                    <h2>Loading...</h2>
                </div>
            )
        )
    }
    const errorMsg = ()=>{
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div 
                    style={{display: error ? "" :"none" }}
                    className="alert alert-danger text-center">
                        {JSON.stringify(error)}
                    </div>
                </div>
            </div>
        )
    }

    const signInForm= ()=>{
        return (
            <div className="row">
                <div className="col-md-6 text-left offset-sm-3">
                    <form>
                        <div className="form-group">
                            <label className="text-light">New Password</label>
                            <input value={password} onChange={handleChange("password")} type="email" className="form-control"/>
                        </div>
                        <button onClick={onSubmit} className="btn btn-success btn-block">Change Password</button>
                    </form>
                </div>
            </div>
        );
    }

    const onSubmit=(event)=>{
        event.preventDefault()
        setValue({...values,error:false,isLoading:true})
        //TODO: Methord
    }

    return (
        <Base title="Sign In">
            {successMsg()}
            {errorMsg()}
            {signInForm()}
            {performRedirect()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    )
}
