import React, { useState } from 'react'
import Base from '../re_usable/Base'
import { Redirect } from 'react-router-dom'
import {fogortpass,veifyotp} from './helper/auth_helper'

export default function Forgot_Pass() {
    const [values,setValue] = useState({
        email:"",
        otp:"",
        error:"",
        success:false,
        otpSend:false,
        isLoading:false,
        didRedirect:false
    })

    const {email,otp,success,error,otpSend,isLoading,didRedirect}=values

    const handleChange =(field)=>(event)=>{
        setValue({...values,success:false,error:false,[field]:event.target.value})
    }

    const performRedirect = ()=>{
        if(didRedirect){
            return <Redirect to="/otp_verify" />
        }
    }

    const loadingMsg =()=>{
        return (
            isLoading && (
                <div className="row">
                    <div className="col-md-6 offset-sm-3 text-left">
                        <div className="alert alert-info text-center">
                            Loading...
                        </div>
                    </div>
                </div>
            )
        )
    }

    const successMsg =()=>{
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div 
                    style={{display: success ? "" :"none" }}
                    className="alert alert-success text-center">
                        {JSON.stringify(success)}
                    </div>
                </div>
            </div>
        )
    }
    const errorMsg = ()=>{
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div 
                    style={{display: error ? "" :"none" }}
                    className="alert alert-danger text-center">
                        {JSON.stringify(error)}
                    </div>
                </div>
            </div>
        )
    }

    const sendOTPForm= ()=>{
        return (
            <div className="row">
                <div className="col-md-6 text-left offset-sm-3">
                    <form>
                        <div className="form-group">
                            <label>Email</label>
                            <input value={email} onChange={handleChange("email")} type="email" className="form-control"/>
                        </div>
                        <button onClick={onSubmitEmail} className="btn btn-success btn-block">Send OTP</button>
                    </form>
                </div>
            </div>
        );
    }

    const otpVerifyForm= ()=>{
        return (
            <div className="row">
                <div className="col-md-6 text-left offset-sm-3">
                    <form>
                        <div className="form-group">
                            <label>OTP</label>
                            <input value={otp} onChange={handleChange("otp")} type="number" className="form-control"/>
                        </div>
                        <button onClick={onSubmitOTP} className="btn btn-success btn-block mb-2">Verify OTP</button>
                        <button onClick={onResendOTP} className="btn btn-primary btn-block">Resend OTP</button>
                    </form>
                </div>
            </div>
        );
    }

    const onSubmitEmail=(event)=>{
        event.preventDefault()
        setValue({...values,error:false,isLoading:true})
        fogortpass({email})
        .then((responce)=>{
            if(responce.err){
                setValue({...values,error:responce.err,success:false,isLoading:false})
            }else{
                setValue({
                    ...values,
                    error:false,
                    success:"OTP Send To Your Email!",
                    otpSend:true,
                    isLoading:false
                });
            }
        }).catch((err)=>{
            console.log('Failed to send forget reuest');
        })
    }

    const onSubmitOTP=(event)=>{
        event.preventDefault()
        setValue({...values,error:false,success:false,isLoading:true})
        veifyotp({email,otp})
        .then((responce)=>{
            if(responce.err){
                setValue({...values,error:responce.err,isLoading:false})
            }else{
                setValue({
                    ...values,
                    error:"",
                    success:"OTP Varified!",
                    isLoading:false,
                    didRedirect:true
                })
            }
        })
        .catch((err)=>{
            console.log('Faild to send otp verify request');
        })
    }

    const onResendOTP=(event)=>{
        event.preventDefault()
        setValue({...values,
            email:"",
            otp:"",
            error:false,
            success:false,
            isLoading:false,
            otpSend:false
        })
    }

    return !otpSend
    ?(
        <Base title="Forgot Password">
            {loadingMsg()}
            {successMsg()}
            {errorMsg()}
            {sendOTPForm()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    )
    :(
        <Base title="Forgot Password">
            {loadingMsg()}    
            {successMsg()}
            {errorMsg()}
            {otpVerifyForm()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    )
}
