import React, { useState } from 'react'
import {Base} from '../re_usable/Base'
import { Redirect } from 'react-router-dom'

export default function Signin() {

    const [values,setValue] = useState({
        email:"s@sourav.in",
        password:"12345",
        error:"",
        isLoading:false,
        didRedirect:false
    })

    const {email,password,error,isLoading,didRedirect}=values

    const handleChange =(field)=>(event)=>{
        setValue({...values,error:false,[field]:event.target.value})
    }

    const performRedirect = ()=>{
        if(didRedirect){
            return <Redirect to="/" />
        }
        //TODO:
        // if(isAuthenticated()){
        //     return <Redirect to="/"/>
        // }
    }

    const successMsg =()=>{
        return (
            isLoading && (
                <div className="container alert alert-info">
                    <h2>Loading...</h2>
                </div>
            )
        )
    }
    const errorMsg = ()=>{
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div 
                    style={{display: error ? "" :"none" }}
                    className="alert alert-danger text-center">
                        {JSON.stringify(error)}
                    </div>
                </div>
            </div>
        )
    }

    const signInForm= ()=>{
        return (
            <div className="row">
                <div className="col-md-6 text-left offset-sm-3">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input value={email} onChange={handleChange("email")} type="email" className="form-control"/>
                        </div>
                        <div className="form-group">
                            <label className="text-light">Password</label>
                            <input value={password} onChange={handleChange("password")} type="password" className="form-control"/>
                        </div>
                        <button onClick={onForgot} className="btn btn-success btn-block">Forgot Password</button>
                        <button onClick={onSubmit} className="btn btn-success btn-block">Sign In</button>
                    </form>
                </div>
            </div>
        );
    }

    const onSubmit=(event)=>{
        event.preventDefault()
        setValue({...values,error:false,isLoading:true})
        //TODO: Methord
    }
    const onForgot=(event)=>{
        <Redirect to='/fogot_pass'/>
    }

    return (
        <Base title="Sign In">
            {successMsg()}
            {errorMsg()}
            {signInForm()}
            {performRedirect()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    )
}
