import {API} from '../backend'

export const userAPI = (token,id)=>{
    return fetch(`${API}/user/${id}`,{
        method:"GET",
        headers:{
            Authorization:`Bearer ${token}`
        },
    }).then((responce)=>{
        return responce.json()
    }).catch((err)=>{
        console.log(err);
    })
}
